import React from "react";

export default function ControlCard({ title }) {
  const alertStart = () => {
    window.alert("คุณกำลังกด Start" + title);
  };
  const alertRestart = () => {
    window.alert("คุณกำลังกด Restart" + title);
  };
  const alertDown = () => {
    window.alert("คุณกำลังกด Down" + title);
  };
  const titleArray = ["Logistic", "E-Commerce", "Banking", "Computer"];
  const showData = () => {
    return (
      <div>
        <li>Hello</li>
        <li>World</li>
      </div>
    );
  };

  return (
    <div>
      <button className="btn btn-success" onClick={() => alertStart()}>
        Start
        <div>
          <button className="text-base p-2 font-bold bg-green-500 rounded-sm">
            Start
          </button>
        </div>
      </button>
      <button className="btn btn-warning" onClick={() => alertRestart()}>
        Restart
        <div>
          <button className="text-base p-2 font-bold bg-gray-500 rounded-sm">
            Restart
          </button>
        </div>
      </button>
      <button className="btn btn-danger" onClick={() => alertDown()}>
        Down
        <div>
          <button className="text-base p-2 font-bold bg-red-500 rounded-sm">
            Down
          </button>
        </div>
        <div>
          <div>
            {titleArray.map((eachTitleArray) => (
              <ControlCard key={eachTitleArray} title={eachTitleArray} />
            ))}
          </div>
        </div>
      </button>
      {showData()}
    </div>
  );
}
