import React from "react";
import Top from "./Components/Top";
import ControlCard from "./Components/ControlCard";

function App() {
  const titleArray = ["Logistic", "E-Commerce", "Banking"];
  return (
    <div>
      <header className="App-header">
        <div>
          <Top subtitle={"Monitoring System"} />
        </div>
        <div>
          <button className="text-base p-2 font-bold bg-green-500 rounded-sm">
            Start
          </button>
          <button className="text-base p-2 font-bold bg-gray-500 rounded-sm">
            Restart
          </button>
          <button className="text-base p-2 font-bold bg-red-500 rounded-sm">
            Down
          </button>
          {titleArray.map((eachTitleArray) => (
            <ControlCard key={eachTitleArray} title={eachTitleArray} />
          ))}
        </div>
      </header>
    </div>
  );
}

export default App;
