import React from "react";

export default function ControlCard({ title }) {
  const alertStart = () => {
    window.alert("คุณกำลังกด Start " + title);
  };
  const alertRestart = () => {
    window.alert("คุณกำลังกด Restart " + title);
  };
  const alertDown = () => {
    window.alert("คุณกำลังกด Down " + title);
  };
  const showData = () => {
    return (
      <div>
        <li>Hello</li>
        <li>World</li>
      </div>
    );
  };

  return (
    <div>
      <h1> {title} </h1>
      <button class="bg-orange-500" onClick={() => alertStart()}>
        Start
      </button>
      <div>
        {/* <button class="text-base p-2 font-bold bg-blue-700 rounded-sm">
          Start
        </button> */}
      </div>
      <button classname="bg-green-950" onClick={() => alertRestart()}>
        {" "}
        Restart
      </button>
      <button classname="btn btn-danger" onClick={() => alertDown()}>
        Down
      </button>
      {showData()}
    </div>
  );
}
